from limites.Tela import Tela
import PySimpleGUI as sg

class TelaMenu(Tela):
    def __init__(self):
        self.__window = None

    def abrir_menu(self):
        sg.theme('TealMono')
        layout = [
            [sg.Radio('Menu Im√≥veis', group_id="RD1" , key='IMOVEIS')],
            [sg.Radio('Menu Plataformas', group_id="RD1", key='PLATAFORMAS')],
            [sg.Radio('Alterar Senha', group_id="RD1", key='U_SENHA')],
            [sg.Button('Prosseguir' , key='PROXIMO'),
            sg.Button('Sair do Sistema', key='SAIR')]
        ]
        self.__window = sg.Window('Menu').Layout(layout)
        button, values = self.__window.Read()
        for key in values:
            if values[key]:
                escolha = key
                self.__window.Close()
                return button, escolha
        self.__window.Close()
        return button, None



